provider_by_asn.map:
function(doc) { 
    if (doc.doctype == 'Provider') {
    	if (doc.asn_mappings.length > 0) {
    		for (var i in doc.asn_mappings) {
    			var mapping = doc.asn_mappings[i];
    			var len = ! mapping.condition_rdns_suffix ? 0 : mapping.condition_rdns_suffix.length;
    			emit([mapping.asn, len], null);
    		}
    	}
    }
}
